package com.robertgleason.spring6di.controllers;import com.robertgleason.spring6di.services.GreetingService;// Most prefferedpublic class ConstructorInjectedController {    private final GreetingService greetingService;    public ConstructorInjectedController(GreetingService greetingService) {        this.greetingService = greetingService;    }    public String sayHello() {        return greetingService.sayGreeting();    }}