package com.robertgleason.spring6di.controllers;import com.robertgleason.spring6di.services.GreetingService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Controller;// Not Preffered@Controllerpublic class PropertyInjectedController {    @Qualifier("propertyGreetingService")    @Autowired    GreetingService greetingService;    public String sayHello() {        return greetingService.sayGreeting();    }}